  //     password: '',
  //     confirmPassword: '',
  //   });

  //   setInput("");
  // };

  const handleSubmit = async (e) => {
    console.log(input);
    e.preventDefault();

    // Validation
    let formErrors = {};
    if (input.name === "") {
      formErrors.name = "User name is required";
    }

    if (input.email === "") {
      formErrors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(input.email)) {
      formErrors.email = "Email is invalid";
    }

    if (input.password === "") {
      formErrors.password = "Password is required";
    } else if (input.password.length < 6) {
      formErrors.password = "Password must be at least 6 characters long";
    }

    if (input.confirmPassword === "") {
      formErrors.confirmPassword = "Confirm Password is required";
    } else if (input.password !== input.confirmPassword) {
      formErrors.confirmPassword = "Confirm Password does not match";
    }

    if (Object.keys(formErrors).length === 0) {
      // Form is valid, proceed with submission
      setIsFormValid(true);
      console.log(input);
      setSubmit(true);

      try {
        // Sending data to server using axios
        const response = await axios.post(
          "http://127.0.0.1:8000/api/user/register/",
          input,
          {
            headers: {
              "Content-Type": "application/json",
              "Accept": "application/json",
            },
          });

        const result = response.data;
        console.log("results", result);
        localStorage.setItem("token", result.token);

        // Navigate to login after successful registration
        navigate("/login");
      } catch (error) {
        if (error.response) {
          // The request was made and the server responded with a status code
          // that falls out of the range of 2xx
          console.error("Response error:", error.response.data);
        } else if (error.request) {
          // The request was made but no response was received
          console.error("Request error:", error.request);
        } else {
          // Something happened in setting up the request that triggered an Error
          console.error("Error", error.message);
        }
        console.error("Error config:", error.config);

        // console.error('There was a problem with the axios operation:', error);
      }
    } else {
      // Form has errors, update state to display error messages
      setIsFormValid(false);
      setErrors(formErrors);
      setSubmit(false);
    }

    // Reset form input
    setInput({
      name: "",
      phoneNumber: "",
      email: "",
      password: "",
      confirmPassword: "",
    });
  };

  const onGoogleSignIn = async (googleUser) => {
    const id_token = googleUser.getAuthResponse().id_token;

    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/user/google-signin/",
        {
          token: id_token,
        },
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      const data = response.data;
      if (data.success) {
        localStorage.setItem("token", data.token);
        navigate("/home");
      } else {
        console.error("Authentication failed:", data.message);
      }
    } catch (error) {
      console.error("Error:", error.response || error.message);
    }
  };

  const onGoogleSignInFailure = (error) => {
    console.error("Google Sign-In error:", error);
  };

  return (
    <>
      <header>
        <div className="container">
          <div className="signup-container  ">
            <div className="container-body ">
              <form onSubmit={handleSubmit} className="signup-form ">
                <div className="field ">
                  <h2 className="text-overlay">Welcome, to signIn page </h2>
                  <hr />
                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>
                      Full Name:
                    </label>
                    <input
                      type="text"
                      name="name"
                      placeholder="Enter Your Full Name"
                      value={input.name}
                      onChange={handleChange}
                    />
                    {""}
                    {errors.name && <div className="error">{errors.name}</div>}
                  </div>

                  {/* </div>


          <div className="col"> */}
                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>
                      Email:
                    </label>
                    <input
                      type="email"
                      name="email"
                      placeholder=" Enter Your Email"
                      value={input.email}
                      onChange={handleChange}
                    />
                    {errors.email && (
                      <div className="error">{errors.email}</div>
                    )}
                  </div>

                  {/* </div>


                   <div className="col"> */}
                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>
                      Password:
                    </label>
                    <input
                      type="password"
                      name="password"
                      placeholder="Enter Your Password"
                      value={input.password}
                      onChange={handleChange}
                    />
                    {errors.password && (
                      <div className="error">{errors.password}</div>
                    )}
                  </div>

                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>
                      Confirm Password:
                    </label>
                    <input
                      type="password"
                      name="confirmPassword"
                      placeholder="Enter Your Confirm Password"
                      value={input.confirmPassword}
                      onChange={handleChange}
                    />
                    {errors.password && (
                      <div className="error">{errors.confirmPassword}</div>
                    )}
                  </div>
                  <div className="submit-container">
                    <div className="submit">
                      <button className="btn " varient="success" type="submit">
                        Register
                      </button>
                    </div>
                  </div>
                </div>
                <div
                  className="mt-3 d-flex justify-content-center align-items-center"
                  style={{ position: "relative", width: "100%" }}
                >
                  <div
                    style={{
                      flex: "1",
                      borderBottom: "1px solid #000",
                      margin: "0 10px",
                    }}
                  ></div>
                  <p style={{ margin: "0" }}>Or</p>
                  <div
                    style={{
                      flex: "1",
                      borderBottom: "1px solid #000",
                      margin: "0 10px",
                    }}
                  ></div>
                </div>
                <div className="mt-2 d-flex justify-content-center">
                  <p className="mb-3">Sign Up with social platforms : </p>
                  <div id="google-signin-button">
                    <span className="ml-3">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        x="0px"
                        y="0px"
                        width="30"
                        height="30"
                        viewBox="0 0 48 48"
                      >
                        <path
                          fill="#FFC107"
                          d="M43.611,20.083H42V20H24v8h11.303c-1.649,4.657-6.08,8-11.303,8c-6.627,0-12-5.373-12-12c0-6.627,5.373-12,12-12c3.059,0,5.842,1.154,7.961,3.039l5.657-5.657C34.046,6.053,29.268,4,24,4C12.955,4,4,12.955,4,24c0,11.045,8.955,20,20,20c11.045,0,20-8.955,20-20C44,22.659,43.862,21.35,43.611,20.083z"
                        ></path>
                        <path
                          fill="#FF3D00"
                          d="M6.306,14.691l6.571,4.819C14.655,15.108,18.961,12,24,12c3.059,0,5.842,1.154,7.961,3.039l5.657-5.657C34.046,6.053,29.268,4,24,4C16.318,4,9.656,8.337,6.306,14.691z"
                        ></path>
                        <path
                          fill="#4CAF50"
                          d="M24,44c5.166,0,9.86-1.977,13.409-5.192l-6.19-5.238C29.211,35.091,26.715,36,24,36c-5.202,0-9.619-3.317-11.283-7.946l-6.522,5.025C9.505,39.556,16.227,44,24,44z"
                        ></path>
                        <path
                          fill="#1976D2"
                          d="M43.611,20.083H42V20H24v8h11.303c-0.792,2.237-2.231,4.166-4.087,5.571c0.001-0.001,0.002-0.001,0.003-0.002l6.19,5.238C36.971,39.205,44,34,44,24C44,22.659,43.862,21.35,43.611,20.083z"
                        ></path>
                      </svg>
                    </span>
                  </div>
                </div>

                <div className="mt-3 d-flex justify-content-center">
                  <p className="ml-2">
                    Already have an account? <a href="/login">Login</a>
                  </p>
                </div>
              </form>
            </div>
          </div>
        </div>
        {/* <div className="signup-image">
          <img src="./assets/img/old1.png " alt="Sign Up" />
          <div className='mt-3   '>
            <p>Something here.... </p>
          </div>
        </div> */}
      </header>
    </>
  );
};

export default SignUp;
