import React, { useContext, useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import "./login.css";
import axios from "axios";

const Login = ({ isLoggedIn }) => {
  const [input, setInput] = useState({
    username: "",
    password: "",
  });

  const navigate = useNavigate();
  const location = useLocation();
  const redirectPath = location.state?.path || "/";
  const [loggedIn, setLoggedIn] = useState(false);
  const [submit, setSubmit] = useState({ ...input });
  const [error, setError] = useState({ ...input });

  useEffect(() => {
    // Check if user is already logged in

    setLoggedIn(true);
    console.log(loggedIn);
  }, [loggedIn]);

  useEffect(() => {
    // Check if user is already logged in
    const token = localStorage.getItem("login");
    //if user is not logged in back to login page
    if (!token) {
      setLoggedIn(true);
      navigate("/login");
    }

    axios
      .get("https://localhost:3000/")
      .then((response) => {
        console.log(response.data);
        setInput(response.data);
        setSubmit(true);
      })

      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleChange = (e) => {
    setInput({
      ...input,
      [e.target.name]: e.target.value,
    });
    setError({
      ...error,
      [e.target.name]: e.target.value ? "" : "Required Field",
    });
  };

  const handleClick = () => navigate("/signUp");

  const handleSubmit = async (e) => {
    e.preventDefault();
    //  username ma value set garxa

    setSubmit({
      submit: true,
      ...input,
    });
    setLoggedIn(true);
    setInput("");
    try {
      const response = await axios.post("login_API_Here", input);
      localStorage.setItem("token", response.data.token);
      setLoggedIn(true);
      isLoggedIn(false);
      navigate.push("/home"); // Redirect to home after successful login
    } catch (error) {
      console.log(error);
      // Handle login error
    }
  };

  return (
    <>
      <header>
        <div className="container text-center">
          <div className="row align-items-center">
            <div className=" col login-logo  mb-0">
              <img
                src="./assets/img/aviyanLogo.png"
                alt="Logo "
                className="logo-img"
              />
              <h2>
                {" "}
                <div className="quote">
                  "Living with PCOS is a journey of resilience and strength.
                  Every step forward is a victory, and every challenge faced is
                  a testament to your courage."
                </div>
              </h2>
            </div>
            <div className="col">
              <form
                action=""
                method="post"
                onSubmit={handleSubmit}
                className="login-form"
              >
                <div className="form_body ">
                  <h2>Welcome to Sakhi</h2>
                  <h3>Please log into the form</h3>
                  <input
                    className="login-input"
                    type="email"
                    id="email"
                    name="username"
                    placeholder="Email "
                    value={input.username}
                    onChange={handleChange}
                    required
                  />
                  <input
                    className="login-input"
                    type="password"
                    id="password"
                    name="password"
                    placeholder="Password"
                    value={input.password}
                    onChange={handleChange}
                    required
                  />
                  <div className="col d-flex justify-content-between">
                    <Link to="/login/resetotp">
                      <span>Forget password</span>
                    </Link>
                    <Link to="/login/changepassword">
                    <span>Change password</span>
                    </Link>
                  </div>
                  <div className="row  align-items-center">
                    <div className="col">
                      <Link to="/">
                        <button type="submit" className="login-btn">
                          Login
                        </button>
                      </Link>
                    </div>
                  </div>
                  <hr />
                  Already Have Account?
                  <button className="create" onClick={handleClick}>
                    Sign In
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </header>
    </>
  );
};

export default Login;
