//SignUp.js
import { React, useEffect, useState } from 'react';

import './signup.css';
import axios from 'axios';
import {  useNavigate } from 'react-router-dom';

const SignUp = () => {

  const [input, setInput] = useState({
    name: '',

    email: '',
    password: '',
    confirmPassword: '',
  });
  const navigate = useNavigate()
  const [posts, setPosts] = useState([]);
  const [submit, setSubmit] = useState(false)
  const [isFormValid, setIsFormValid] = useState(false);

  const [errors, setErrors] = useState({});




  const handleChange = (e) => {
    const value = e.target.value;
    const name = e.target.name;
    if (name === 'name') {
      setInput({
        ...input,
        name: value,
      })
    }


    else if (name === 'email') {
      setInput({
        ...input,
        email: value,
      })
    }
    else if (name === 'password') {
      setInput({
        ...input,
        password: value,
      })
    }
    else if (name === 'confirmPassword') {
      setInput({
        ...input,
        confirmPassword: value,
      })
    }
  }

  // async const handleSubmit =  (e) => {
  //   console.log(input);
  //   e.preventDefault();
  //   //  validation
  //   let formErrors = {};
  //   if (input.name === "") {
  //     formErrors.name = 'User name is required';

  //   }


  //   if (input.email === "") {
  //     formErrors.email = 'Email is required';

  //   } else if (!/\S+@\S+\.\S+/.test(input.email)) {
  //     formErrors.email = 'Email is invalid';

  //   }
  //   if (input.password === "") {
  //     formErrors.password = 'Password is required';

  //   } else if (input.password.length < 6) {

  //     formErrors.password = 'Password must be at least 6 characters long';
  //   }
  //   if (input.confirmPassword === "") {
  //     formErrors.confirmPassword = 'Confirm Password is required';

  //   } else if (!input.password === input.confirmPassword) {
  //     formErrors.confirmPassword = 'Confirm Password does not match';

  //   }

  //   if (Object.keys(formErrors)?.length === 0) {
  //     // Form is valid, proceed with submission
  //     setIsFormValid(true);
  //     console.log(input);
  //     setSubmit(true);
  //     navigate("/login");
  //   } else {
  //     // Form has errors, update state to display error messages
  //     setIsFormValid(false)
  //     setErrors(formErrors);
  //     setSubmit(false);
  //   }

  //   // sending data to server
  //   let result = Await(fetch('http://127.0.0.1:8000/api/user/register/', {
  //     method: "Post",
  //     body: JSON.stringify(input),
  //     headers: {
  //       'Content-Type': 'application/json',
  //       'Accept': 'application/json'
  //     }
  //   }))
  //   result = Await(result.json())
  //   console.log("results", result)
  //   localStorage.setItem('token', result.token)

  //   // axios.post('http://localhost:3000/signup')
  //   //   .then((response) => {
  //   //     console.log(response.data);
  //   //     setPosts(response.data)
  //   //     setSubmit(true)


  //   //   })
  //   // .catch((error) => {
  //   //   console.log(error);
  //   // })

  //   setInput({
  //     name: '',
  //     phoneNumber: '',
  //     email: '',
  //     password: '',
  //     confirmPassword: '',
  //   });


  //   setInput("");
  // };

  

const handleSubmit = async (e) => {
  console.log(input);
  e.preventDefault();

  // Validation
  let formErrors = {};
  if (input.name === "") {
    formErrors.name = 'User name is required';
  }

  if (input.email === "") {
    formErrors.email = 'Email is required';
  } else if (!/\S+@\S+\.\S+/.test(input.email)) {
    formErrors.email = 'Email is invalid';
  }

  if (input.password === "") {
    formErrors.password = 'Password is required';
  } else if (input.password.length < 6) {
    formErrors.password = 'Password must be at least 6 characters long';
  }

  if (input.confirmPassword === "") {
    formErrors.confirmPassword = 'Confirm Password is required';
  } else if (input.password !== input.confirmPassword) {
    formErrors.confirmPassword = 'Confirm Password does not match';
  }

  if (Object.keys(formErrors).length === 0) {
    // Form is valid, proceed with submission
    setIsFormValid(true);
    console.log(input);
    setSubmit(true);

    try {
      // Sending data to server using axios
      const response = await axios.post('http://127.0.0.1:8000/api/user/register/', input, {
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      });

      const result = response.data;
      console.log("results", result);
      localStorage.setItem('token', result.token);

      // Navigate to login after successful registration
      navigate("/login");
    } catch (error) {
      console.error('There was a problem with the axios operation:', error);
    }
  } else {
    // Form has errors, update state to display error messages
    setIsFormValid(false);
    setErrors(formErrors);
    setSubmit(false);
  }

  // Reset form input
  setInput({
    name: '',
    phoneNumber: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
};

