                      type="password"
                      name="confirmPassword"
                      placeholder="Enter Your Confirm Password"
                      value={input.confirmPassword}
                      onChange={handleChange}
                    />
                    {errors.password && <div className="error">{errors.confirmPassword}</div>}
                  </div>
                  <div className='submit-container'>
                    <div className='submit'>
                      <button className='btn ' varient='success' type="submit"  >Register</button>
                    </div>
                  </div>

                </div>
                <div className='mt-3 d-flex justify-content-center align-items-center' style={{ position: 'relative', width: '100%' }}>
                  <div style={{ flex: '1', borderBottom: '1px solid #000', margin: '0 10px' }}></div>
                  <p style={{ margin: '0' }}>Or</p>
                  <div style={{ flex: '1', borderBottom: '1px solid #000', margin: '0 10px' }}></div>
                </div>
                <div className='mt-2 d-flex justify-content-center'>
                  <p className='mb-3'>Sign Up with social platforms : </p>
