//SignUp.js
import { React, useEffect, useState } from 'react';

import './signup.css';
// import axios from 'axios';
import { Await, useNavigate } from 'react-router-dom';
const SignUp = () => {

  const [input, setInput] = useState({
    name: '',

    email: '',
    password: '',
    confirmPassword: '',
  });
  const navigate = useNavigate()
  const [posts, setPosts] = useState([]);
  const [submit, setSubmit] = useState(false)
  const [isFormValid, setIsFormValid] = useState(false);

  const [errors, setErrors] = useState({});




  const handleChange = (e) => {
    const value = e.target.value;
    const name = e.target.name;
    if (name === 'name') {
      setInput({
        ...input,
        name: value,
      })
    }


    else if (name === 'email') {
      setInput({
        ...input,
        email: value,
      })
    }
    else if (name === 'password') {
      setInput({
        ...input,
        password: value,
      })
    }
    else if (name === 'confirmPassword') {
      setInput({
        ...input,
        confirmPassword: value,
      })
    }
  }

  const handleSubmit = async (e) => {
    console.log(input);
    e.preventDefault();
    //  validation
    let formErrors = {};
    if (input.name === "") {
      formErrors.name = 'User name is required';

    }


    if (input.email === "") {
      formErrors.email = 'Email is required';

    } else if (!/\S+@\S+\.\S+/.test(input.email)) {
      formErrors.email = 'Email is invalid';

    }
    if (input.password === "") {
      formErrors.password = 'Password is required';

    } else if (input.password.length < 6) {

      formErrors.password = 'Password must be at least 6 characters long';
    }
    if (input.confirmPassword === "") {
      formErrors.confirmPassword = 'Confirm Password is required';

    } else if (!input.password === input.confirmPassword) {
      formErrors.confirmPassword = 'Confirm Password does not match';

    }

    if (Object.keys(formErrors)?.length === 0) {
      // Form is valid, proceed with submission
      setIsFormValid(true);
      console.log(input);
      setSubmit(true);
      navigate("/login");
    } else {
      // Form has errors, update state to display error messages
      setIsFormValid(false)
      setErrors(formErrors);
      setSubmit(false);
    }

    // sending data to server
    let result = Await(fetch('http://127.0.0.1:8000/api/user/register/', {
      method: "Post",
      body: JSON.stringify(input),
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      }
    }))
    result = Await(result.json())
    console.log("results", result)
    localStorage.setItem('token', result.token)

    // axios.post('http://localhost:3000/signup')
    //   .then((response) => {
    //     console.log(response.data);
    //     setPosts(response.data)
    //     setSubmit(true)


    //   })
    // .catch((error) => {
    //   console.log(error);
    // })

    setInput({
      name: '',
      phoneNumber: '',
      email: '',
      password: '',
      confirmPassword: '',
    });


    setInput("");
  };


  return (
    <>
      <header>
        <div className="container">


          <div className="signup-container  ">
            <div className="container-body ">

              <form onSubmit={handleSubmit} className='signup-form '>


                <div className="field ">
                  <h2 className='text-overlay'>Welcome, to signIn page </h2>
                  <hr />
                  <div className='form-group'>
                    <label style={{ display: "block", textAlign: "left" }}>Full Name:</label>
                    <input
                      type="text"
                      name="name"
                      placeholder="Enter Your Full Name"
                      value={input.name}
                      onChange={handleChange}
                    />{''}
                    {errors.name && <div className="error">{errors.name}</div>}
                  </div>



                  {/* </div>


          <div className="col"> */}
                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>Email:</label>
                    <input
                      type="email"
                      name="email"
                      placeholder=" Enter Your Email"
                      value={input.email}
                      onChange={handleChange}
                    />
                    {errors.email && <div className="error">{errors.email}</div>}
                  </div>

                  {/* </div>


          <div className="col"> */}
                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>Password:</label>
                    <input
                      type="password"
                      name="password"
                      placeholder="Enter Your Password"
                      value={input.password}
                      onChange={handleChange}
                    />
                    {errors.password && <div className="error">{errors.password}</div>}
                  </div>

                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>Confirm Password:</label>
                    <input
                      type="password"
                      name="confirmPassword"
                      placeholder="Enter Your Confirm Password"
                      value={input.confirmPassword}
                      onChange={handleChange}
                    />
                    {errors.password && <div className="error">{errors.confirmPassword}</div>}
                  </div>

                  <div className='submit'>
                    <button className='btn ' varient='success' type="submit"  >Register</button>
                  </div>
                  <hr />
                  <div className='mt-3 d-flex justify-content-center ' >
                    <div>
                      <p>Or</p>
                      <p>Sign Up with social platforms</p>
                    </div>
                  </div>


                  <div className='google'>
                    <button className='btn ' type="google-register"  >
                      <span>

                        <svg xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="30" height="30" viewBox="0 0 48 48" >
                          <path fill="#FFC107" d="M43.611,20.083H42V20H24v8h11.303c-1.649,4.657-6.08,8-11.303,8c-6.627,0-12-5.373-12-12c0-6.627,5.373-12,12-12c3.059,0,5.842,1.154,7.961,3.039l5.657-5.657C34.046,6.053,29.268,4,24,4C12.955,4,4,12.955,4,24c0,11.045,8.955,20,20,20c11.045,0,20-8.955,20-20C44,22.659,43.862,21.35,43.611,20.083z"></path><path fill="#FF3D00" d="M6.306,14.691l6.571,4.819C14.655,15.108,18.961,12,24,12c3.059,0,5.842,1.154,7.961,3.039l5.657-5.657C34.046,6.053,29.268,4,24,4C16.318,4,9.656,8.337,6.306,14.691z"></path><path fill="#4CAF50" d="M24,44c5.166,0,9.86-1.977,13.409-5.192l-6.19-5.238C29.211,35.091,26.715,36,24,36c-5.202,0-9.619-3.317-11.283-7.946l-6.522,5.025C9.505,39.556,16.227,44,24,44z"></path><path fill="#1976D2" d="M43.611,20.083H42V20H24v8h11.303c-0.792,2.237-2.231,4.166-4.087,5.571c0.001-0.001,0.002-0.001,0.003-0.002l6.19,5.238C36.971,39.205,44,34,44,24C44,22.659,43.862,21.35,43.611,20.083z"></path>
                        </svg>
                      </span>
                      <span> SignUp with Google</span>
                    </button>

                  </div>

                  <div className='mt-3'>
                    <p>Already have an account? <a href="/login">Login</a></p>
                  </div>
                </div>
              </form>

            </div >


          </div >
        </div>
        {/* <div className="signup-image">
          <img src="./assets/img/old1.png " alt="Sign Up" />
          <div className='mt-3   '>
            <p>Something here.... </p>
          </div>
        </div> */}
      </header>
    </>
  );
}

export default SignUp;
