//SignUp.js
import { React, useEffect, useState } from 'react';

import './signup.css';
// import axios from 'axios';
import { useNavigate } from 'react-router-dom';
const SignUp = () => {

  const [input, setInput] = useState({
    name: '',
    
    email: '',
    password: '',
    confirmPassword: '',
  });
  const navigate = useNavigate()
  const [posts, setPosts] = useState([]);
  const [submit, setSubmit] = useState(false)
  const [isFormValid, setIsFormValid] = useState(false);

  const [errors, setErrors] = useState({});




  const handleChange = (e) => {
    const value = e.target.value;
    const name = e.target.name;
    if (name === 'name') {
      setInput({
        ...input,
        name: value,
      })
    }
    
    
    else if (name === 'email') {
      setInput({
        ...input,
        email: value,
      })
    }
    else if (name === 'password') {
      setInput({
        ...input,
        password: value,
      })
    }
    else if (name === 'confirmPassword') {
      setInput({
        ...input,
        confirmPassword: value,
      })
    }
  }

  const handleSubmit = (e) => {
    console.log(input);
    e.preventDefault();
    //  validation
    let formErrors = {};
    if (input.name === "") {
      formErrors.name = 'User name is required';

    }
    
   
    if (input.email === "") {
      formErrors.email = 'Email is required';

    } else if (!/\S+@\S+\.\S+/.test(input.email)) {
      formErrors.email = 'Email is invalid';

    }
    if (input.password === "") {
      formErrors.password = 'Password is required';

    } else if (input.password.length < 6) {

      formErrors.password = 'Password must be at least 6 characters long';
    }
    if (input.confirmPassword === "") {
      formErrors.confirmPassword = 'Confirm Password is required';

    } else if (!input.password === input.confirmPassword) {
      formErrors.confirmPassword = 'Confirm Password does not match';

    }

    if (Object.keys(formErrors)?.length === 0) {
      // Form is valid, proceed with submission
      setIsFormValid(true);
      console.log(input);
      setSubmit(true);
      navigate("/login");
    } else {
      // Form has errors, update state to display error messages
      setIsFormValid(false)
      setErrors(formErrors);
      setSubmit(false);
    }

    // sending data to server
    fetch('http://127.0.0.1:8000/api/usignup', {
      method:"Post",
      body: JSON.stringify(input),
      headers: {
        'Content-Type': 'application/json',
      }
    })

    // axios.post('http://localhost:3000/signup')
    //   .then((response) => {
    //     console.log(response.data);
    //     setPosts(response.data)
    //     setSubmit(true)


    //   })
    // .catch((error) => {
    //   console.log(error);
    // })

    setInput({
      name: '',
      phoneNumber: '',
      email: '',
      password: '',
      confirmPassword: '',
    });


    setInput("");
  };


  return (
    <>
      <header>
        <div className="container">


          <div className="signup-container  ">
            <div className="container-body ">

              <form onSubmit={handleSubmit} className='signup-form '>


                <div className="field ">
                  <h2 className='text-overlay'>Welcome, to signIn page </h2>
                  <hr />
                  <div className='form-group'>
                    <label style={{ display: "block", textAlign: "left" }}>Full Name:</label>
                    <input
                      type="text"
                      name="name"
                      placeholder="Enter Your Full Name"
                      value={input.name}
                      onChange={handleChange}
                    />{''}
                    {errors.name && <div className="error">{errors.name}</div>}
                  </div>

                  

                  {/* </div>


          <div className="col"> */}
                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>Email:</label>
                    <input
                      type="email"
                      name="email"
                      placeholder=" Enter Your Email"
                      value={input.email}
                      onChange={handleChange}
                    />
                    {errors.email && <div className="error">{errors.email}</div>}
                  </div>

                  {/* </div>


          <div className="col"> */}
                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>Password:</label>
                    <input
                      type="password"
                      name="password"
                      placeholder="Enter Your Password"
                      value={input.password}
                      onChange={handleChange}
                    />
                    {errors.password && <div className="error">{errors.password}</div>}
                  </div>

                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>Confirm Password:</label>
                    <input
                      type="password"
                      name="confirmPassword"
                      placeholder="Enter Your Confirm Password"
                      value={input.confirmPassword}
                      onChange={handleChange}
                    />
                    {errors.password && <div className="error">{errors.confirmPassword}</div>}
                  </div>

                  <div className='submit'>

                    <button className='btn ' varient='success' type="submit"  >Sign Up</button>


                  </div>
                  <div className='mt-3'>
                    <p>Already have an account? <a href="/login">Login</a></p>
                </div>
                </div>
              </form>

            </div >


          </div >
        </div>
        <div className="signup-image">
          <img src="./assets/img/old1.png " alt="Sign Up" />
          <div className='mt-3   '>
            <p>Something here.... </p>
          </div>
        </div>
      </header>
    </>
  );
}

export default SignUp;
