//SignUp.js
import { React, useEffect, useState } from "react";

import "./signup.css";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

const SignUp = () => {
  const [input, setInput] = useState({
    name: "",
    email: "",
    password: "",
    confirmPassword: "",
  });
  const navigate = useNavigate();
  const [errors, setErrors] = useState({});
  // const [posts, setPosts] = useState([]); 
  const [submit, setSubmit] = useState(false) ;
  const [isFormValid, setIsFormValid] = useState(false);

  useEffect(() => {
    const initializeGoogleSignIn = () => {
      window.google.accounts.id.initialize({
        client_id: '391082617038-81ad5f9mn36u9rfarj1b2qjnppb2du8m.apps.googleusercontent.com',
        callback: onGoogleSignIn,
      });
      window.google.accounts.id.renderButton(
        document.getElementById('google-signin-button'),
        { theme: 'outline', size: 'large' }
      );
    };

    if (window.google && window.google.accounts) {
      initializeGoogleSignIn();
    } else {
      const script = document.createElement('script');
      script.src = 'https://accounts.google.com/gsi/client';
      script.async = true;
      script.defer = true;
      script.onload = initializeGoogleSignIn;
      document.body.appendChild(script);
    }
  }, []);


//  Change In Input Box is handled here.....
  const handleChange = (e) => {
    const value = e.target.value;
    const name = e.target.name;
    if (name === "name") {
      setInput({
        ...input,
        name: value,
        errors
      });
    } else if (name === "email") {
      setInput({
        ...input,
        email: value,
      });
    } else if (name === "password") {
      setInput({
        ...input,
        password: value,
      });
    } else if (name === "confirmPassword") {
      setInput({
        ...input,
        confirmPassword: value,
      });
    }
   
  };


//  Form Submission is handled  here........
  const handleSubmit = async (e) => {
    console.log(input);
    e.preventDefault();

    // form Validation.................
    let formErrors = {};
    if (input.name === "") {
      formErrors.name = "User name is required";
    }

    if (input.email === "") {
      formErrors.email = "Email is required";
    } else if (!/\S+@\S+\.\S+/.test(input.email)) {
      formErrors.email = "Email is invalid";
    }

    if (input.password === "") {
      formErrors.password = "Password is required";
    } else if (input.password.length < 6) {
      formErrors.password = "Password must be at least 6 characters long";
    }

    if (input.confirmPassword === "") {
      formErrors.confirmPassword = "Confirm Password is required";
    } else if (input.password !== input.confirmPassword) {
      formErrors.confirmPassword = "Password and Confirm Password does not match";
    }

    if (Object.keys(formErrors).length === 0) {
      // Form is valid, proceed with submission............
      setIsFormValid(true);
      console.log(input);
      setSubmit(true);
      setErrors("");
      

      try {
        // Sending data to server using axios..............
        const response = await axios.post(
          "http://127.0.0.1:8000/api/user/register/",
          input,
          {
            headers: {
              "Content-Type": "application/json",
              "Accept": "application/json",
            },
          });

        const result = response.data;
        console.log("results", result);
        localStorage.setItem("token", result.token);

        // Navigate to login after successful registration.................
        navigate("/login");
      } catch (error) {
        if (error.response) {
          // The request was made and the server responded with a status code
          // that falls out of the range of 2xx
          console.error("Response error:", error.response.data);
        } else if (error.request) {
          // The request was made but no response was received
          console.error("Request error:", error.request);
        } else {
          // Something happened in setting up the request that triggered an Error
          console.error("Error", error.message);
        }
        console.error("Error config:", error.config);

        // console.error('There was a problem with the axios operation:', error);
      }
    } else {
      // Form has errors, update state to display error messages..............
      setIsFormValid(false);
      setErrors(formErrors);
      setSubmit(false);
    }

    // Reset form input
    setInput({
      name: "",
      email: "",
      password: "",
      confirmPassword: "",
    });
  };

// Google Sign In Function handling...............................
  const onGoogleSignIn = async (response) => {
    const id_token = response.credential;

    try {
      const res = await axios.post('http://127.0.0.1:8000/api/user/google-signin/', {
        token: id_token,
      }, {
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const data = res.data;
      if (data.success) {
        navigate("/");
        console.log('Authentication successful:', data);  
        localStorage.setItem('token', data.token);
        
      } else {
        console.error('Authentication failed:', data.message);
      }
    } catch (error) {
      console.error('Error:', error.response || error.message);
    }
  };

  

  return (
    <>
      <header>
        <div className="container">
          <div className="signup-container  ">
            <div className="container-body ">
              <form onSubmit={handleSubmit} className="signup-form ">
                <div className="field ">
                  <h2 className="text-overlay">Welcome, to signIn page </h2>
                  <hr />
                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>
                      Full Name:
                    </label>
                    <input
                      type="text"
                      name="name"
                      placeholder="Enter Your Full Name"
                      value={input.name}
                      onChange={handleChange}
                    />
                    {""}
                    {errors.name && <div className="error">{errors.name}</div>}
                  </div>

                 
                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>
                      Email:
                    </label>
                    <input
                      type="email"
                      name="email"
                      placeholder=" Enter Your Email"
                      value={input.email}
                      onChange={handleChange}
                    />
                    {errors.email && (
                      <div className="error">{errors.email}</div>
                    )}
                  </div>

                  
                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>
                      Password:
                    </label>
                    <input
                      type="password"
                      name="password"
                      placeholder="Enter Your Password"
                      value={input.password}
                      onChange={handleChange}
                    />
                    {errors.password && (
                      <div className="error">{errors.password}</div>
                    )}
                  </div>

                  <div className="form-group">
                    <label style={{ display: "block", textAlign: "left" }}>
                      Confirm Password:
                    </label>
                    <input
                      type="password"
                      name="confirmPassword"
                      placeholder="Enter Your Confirm Password"
