import React, { useEffect } from "react";
import "./resetotp.css";

const ResetOTP = () => {

    const [timer, setTimer] = useState(60); // Initial timer value (e.g., 60 seconds)
    const [isResendDisabled, setIsResendDisabled] = useState(true);
  
    useEffect(() => {
      if (timer > 0) {
        const intervalId = setInterval(() => {
          setTimer((prevTimer) => prevTimer - 1);
        }, 1000);
        return () => clearInterval(intervalId);
      } else {
        setIsResendDisabled(false);
      }
    }, [timer]);
  
    const handleResendCode = () => {
      setTimer(60); // Reset timer to 60 seconds
      setIsResendDisabled(true);
      // Add logic to resend the code here
    };
  

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-9 offset-md-3">
          <div className="card">
            <div className="card-body">
              <h1>Reset OTP</h1>
              <p>Please enter the OTP sent to your email.</p>
              <form>
                <div className="form-group">
                  <label htmlFor="otp">OTP</label>
                  <input
                    type="text"
                    className="form-control"
                    id="otp"
                    placeholder="Enter OTP"
                  />
                </div>
                {/* <div className="form-group">
                  <label htmlFor="password">Password</label>
                  <input
                    type="password"
                    className="form-control"
                    id="password"
                    placeholder="Enter Password"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="confirmPassword">Confirm Password</label>
                  <input
                    type="password"
                    className="form-control"
                    id="confirmPassword"
                    placeholder="Confirm Password"
                  />
                </div> */}
                <button onClick={handleResendCode} disabled={isResendDisabled}>
              Resend Code
            </button>
            <span className="timer">{timer > 0 ? `Resend available in ${timer}s` : 'You can resend the code now'}</span>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
export default ResetOTP;
